dition.foundCondition.fractionMin = Convert.ToSingle(readData["fractionMin"]); itemDef.condition.foundCondition.fractionMax = Convert.ToSingle(readData["fractionMax"]); } } foreach (var item in ItemManager.GetItemDefinitions().Where(i => i.condition.enabled)) { if (!(itemLootFractions["FoundContitions"] as Dictionary<string, object>).ContainsKey(item.shortname)) { fractionOverrides.Add(item.shortname, new Dictionary<string, float> { ["fractionMin"] = item.condition.foundCondition.fractionMin, ["fractionMax"] = item.condition.foundCondition.fractionMax }); changed = true; } } if (changed) { itemLootFractions.Set("FoundContitions", fractionOverrides); itemLootFractions.Save(); } } itemLootFractions.Clear(); }  void LoadGunConfig() { weaponAmmoConfig = (Dictionary<string, object>)Config["WeaponSpawnSetup"]; if (weaponAmmoConfig != null && weaponAmmoConfig.Count() > 0) { var chkweapons = ItemManager.GetItemDefinitions().Where(p => p.category == ItemCategory.Weapon); foreach (var weapon in chkweapons) { if (!guidToPathCopy.ContainsKey(weapon.GetComponent<ItemModEntity>().entityPrefab.guid)) continue; if (weaponAmmoConfig.ContainsKey(weapon.shortname)) continue; var item = ItemManager.Create(weapon); if (item.GetHeldEntity() is BaseProjectile) { Dictionary<string, object> wData = new Dictionary<string, object>(); wData.Add("xtraRoundsEnabled", false); wData.Add("roundsWeaponAmount", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.contents); wData.Add("roundsWeaponType", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.ammoType.shortname); wData.Add("xtraRoundsAmount", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.definition.builtInSize); wData.Add("xtraRoundsType", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.ammoType.shortname); weaponAmmoConfig.Add(weapon.shortname, wData); } item.Remove(0f); } Config["WeaponSpawnSetup"] = weaponAmmoConfig; Config.Save(); return; } else { weaponAmmoConfig = new Dictionary<string, object>(); var weapons = ItemManager.GetItemDefinitions().Where(p => p.category == ItemCategory.Weapon); foreach (var weapon in weapons) { if (!guidToPathCopy.ContainsKey(weapon.GetComponent<ItemModEntity>().entityPrefab.guid)) continue; var item = ItemManager.CreateByName(weapon.shortname); if (item.GetHeldEntity() is BaseProjectile) { Dictionary<string, object> wData = new Dictionary<string, object>(); wData.Add("xtraRoundsEnabled", false); wData.Add("roundsWeaponAmount", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.contents); wData.Add("roundsWeaponType", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.ammoType.shortname); wData.Add("xtraRoundsAmount", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.definition.builtInSize); wData.Add("xtraRoundsType", weapon.GetComponent<ItemModEntity>().entityPrefab.Get().GetComponent<BaseProjectile>().primaryMagazine.ammoType.shortname); weaponAmmoConfig.Add(weapon.shortname, wData); } item.Remove(0f); } Config["WeaponSpawnSetup"] = weaponAmmoConfig; Config.Save(); } }  void LoadAllContainers() { var filter = Oxide.Game.Rust.RustExtension.Filter.ToList(); filter.Add("[BUNDLE] Not found in bundle"); Oxide.Game.Rust.RustExtension.Filter = filter.ToArray(); var allTypes = Resources.FindObjectsOfTypeAll<GameObject>().Where(lc => lc.GetComponent<LootContainer>() != null).GroupBy(l => l.GetComponent<LootContainer>().ShortPrefabName).Select(g => g.First()).ToList(); filter = Oxide.Game.Rust.RustExtension.Filter.ToList(); filter.Remove("[BUNDLE] Not found in bundle"); Oxide.Game.Rust.RustExtension.Filter = filter.ToArray(); LootContainer loot = null; foreach (var allType in allTypes) { loot = allType.GetComponent<LootContainer>(); if (loot == null || loot.ShortPrefabName == string.Empty || loot.PrefabName == string.Empty) continue; if (AllContainerTypes.ContainsKey(loot.ShortPrefabName)) continue; if (enableBradleyLoadOut && loot.ShortPrefabName == "bradley_crate") continue; if (enableHeliLoadOut && loot.ShortPrefabName == "heli_crate") continue; if (enableFancyDropLoot && loot.ShortPrefabName == "supply_drop" && FancyDrop) continue; if (loot.lootDefinition == null) if (loot.LootSpawnSlots.Length == 0) continue; AllContainerTypes[loot.ShortPrefabName] = loot.PrefabName; if (!AllLootSpawns.ContainsKey(loot.ShortPrefabName) && loot.lootDefinition != null) AllLootSpawns[loot.ShortPrefabName] = loot.lootDefinition; if (!AllLootSlots.ContainsKey(loot.ShortPrefabName) && loot.LootSpawnSlots.Length > 0) AllLootSlots[loot.ShortPrefabName] = loot.LootSpawnSlots; } loot = null; }  [ConsoleCommand("al.reload")] void consoleReload(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; manualReload = true; LoadConfig(); LoadVariables(); NextFrame(()=>ServerMgr.Instance.StartCoroutine(UpdateInternals(arg : arg))); }  [ConsoleCommand("al.enable")] void consoleEnable(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (pluginEnabled && (bool)Config["Generic", "pluginEnabled"]) { if (firstSetup) { firstSetup = false; NextFrame(() => { ServerMgr.Instance.StartCoroutine(UpdateInternals(arg : arg)); SendReplyCl(arg, "Plugin is now active and running"); }); return; } SendReplyCl(arg, "Plugin already active and running"); firstSetup = false; Changed = false; return; } LoadConfig(); LoadVariables(); Config["Generic", "pluginEnabled"] = true; pluginEnabled = true; SaveConfig(); firstSetup = false; NextFrame(() => { ServerMgr.Instance.StartCoroutine(UpdateInternals(arg : arg)); SendReplyCl(arg, "Plugin is now active and running"); }); }  [ConsoleCommand("al.disable")] void consoleDisable(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (!pluginEnabled && !(bool)Config["Generic", "pluginEnabled"]) { SendReplyCl(arg, "Plugin already disabled"); return; } LoadConfig(); LoadVariables(); Config["Generic", "pluginEnabled"] = false; pluginEnabled = false; SaveConfig(); SendReplyCl(arg, "Plugin is now disabled"); }  [ConsoleCommand("al.repopulateloot")] void consoleRepopulate(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (!pluginEnabled) { SendReply(arg, "Plugin is disabled by config"); return; } int populatedContainers = 0; NextFrame(()=> { foreach (var container in UnityEngine.Object.FindObjectsOfType<LootContainer>().ToList()) { if (enableHeliLoadOut && container.ShortPrefabName == "heli_crate") { if (PopulateSpecial(container, true)) populatedContainers++; continue; } if (enableBradleyLoadOut && container.ShortPrefabName == "bradley_crate") { if (PopulateSpecial(container, true)) populatedContainers++; continue; } if (enableFancyDropLoot && container is SupplyDrop && FancyDrop) continue; if (PopulateContainer(container, true)) populatedContainers++; } SendReply(arg, $"Repopulated '{populatedContainers}' LootContainer"); }); }  [ConsoleCommand("al.rarityreload")] void consoleRarityReload(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; LoadItemRaritys(); SendReply(arg, $"New raritys will be active after running 'al.reload'"); }  [ConsoleCommand("al.rarityset")] void consoleRaritySet(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (arg.Args == null || arg.Args.Length <= 1 || arg.Args.Length > 2) { SendReply(arg, "Example: al.rarityset <name> <x|rarity-num> "); return; } ItemDefinition def = null; if (arg.Args != null && arg.Args.Length > 1) { def = ItemManager.FindItemDefinition(arg.Args[0]); if (def == null) { SendReply(arg, $"Item \"{arg.Args[0]}\" not found"); return; } int num = -1; if (arg.Args.Length > 1) if (!int.TryParse(arg.Args[1], out num)) { SendReply(arg, $"Set a full number for the rarity (0..4)"); return; } if (rarityOverridesItems.ContainsKey(arg.Args[0])) rarityOverridesItems[arg.Args[0]] = num; else rarityOverridesItems.Add(arg.Args[0], num); try { itemRaritys = getFile("ItemRaritys"); } catch (JsonReaderException e) { PrintWarning($"JSON error in 'ItemRaritys' > Line: {e.LineNumber} | {e.Path}"); pluginEnabled = false; return; } itemRaritys.Set("ItemRaritys", rarityOverridesItems); itemRaritys.Set("ItemBlueprintRaritys", rarityOverridesBlueprints); itemRaritys.Save(); itemRaritys.Clear(); SendReply(arg, $"The new rarity will be active after running 'al.reload'"); } }  [ConsoleCommand("al.raritysetbp")] void consoleRaritySetBp(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (arg.Args == null || arg.Args.Length <= 1 || arg.Args.Length > 2) { SendReply(arg, "Example: al.raritysetbp <name> <x|rarity-num> "); return; } ItemBlueprint defBP = null; if (arg.Args != null && arg.Args.Length > 1) { defBP = ItemManager.FindBlueprint(ItemManager.FindItemDefinition(arg.Args[0])); if (defBP == null) { SendReply(arg, $"Item \"{arg.Args[0]}\" not found"); return; } int num = -1; if (arg.Args.Length > 1) if (!int.TryParse(arg.Args[1], out num)) { SendReply(arg, $"Set a full number for the rarity (0..4)"); return; } if (rarityOverridesBlueprints.ContainsKey(arg.Args[0])) rarityOverridesBlueprints[arg.Args[0]] = num; else rarityOverridesBlueprints.Add(arg.Args[0], num); try { itemRaritys = getFile("ItemRaritys"); } catch (JsonReaderException e) { PrintWarning($"JSON error in 'ItemRaritys' > Line: {e.LineNumber} | {e.Path}"); pluginEnabled = false; return; } itemRaritys.Set("ItemRaritys", rarityOverridesItems); itemRaritys.Set("ItemBlueprintRaritys", rarityOverridesBlueprints); itemRaritys.Save(); itemRaritys.Clear(); SendReply(arg, $"The new rarity will be active after running 'al.reload'"); } }  void InitConNames() => Author = GetConName("ShWvXhEn");  [ConsoleCommand("al.lootreset")] void consoleLootReset(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; string data = string.Empty; if (arg.Args == null || arg.Args.Length < 1) { SendReply(arg, "\nWill reset/recalculate itemlists to their defaults based on any given 'UsedMultiplier's\nYou need to provide 'all', 'tables', 'heli', 'bradley' or 'fancy'"); return; } data = arg.Args[0]; try { lootTable = getFile("LootTables"); } catch (JsonReaderException e) { PrintWarning($"JSON error in 'LootTables' > Line: {e.LineNumber} | {e.Path}"); pluginEnabled = false; return; } if (data == "all" || data == "tables") { var lootcont = lootTable.Get("LootContainer"); lootTable.Clear(); lootTable.Set("LootContainer", lootcont); lootTable.Save(); LoadConfig(); LoadVariables(); pluginEnabled = false; Config["Generic", "pluginEnabled"] = false; Config.Save(); } NextFrame(()=> { if (data == "all") { ServerMgr.Instance.StartCoroutine(UpdateInternals(true, true, true, true, arg : arg)); SendReply(arg, $"All loot contents successfully recreated"); } else if (data == "tables") { ServerMgr.Instance.StartCoroutine(UpdateInternals(true, arg : arg)); SendReply(arg, $"LootTables successfully recreated"); } else if (data == "heli") { GetHeliLoot(true); SendReply(arg, $"HeliLoot successfully recreated"); } else if (data == "bradley") { GetBradleyLoot(true); SendReply(arg, $"BradleyLoot successfully recreated"); } else if (data == "fancy") { GetFancyDropLoot(new Dictionary<string, object>(), true); SendReply(arg, $"FancyDropLoot successfully recreated"); } else SendReply(arg, "You need to provide 'all', 'tables', 'heli', 'bradley' or 'fancy'"); }); }  [ConsoleCommand("al.lootmultiplier")] void consoleLootMultiplier(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; string data = string.Empty; if (arg.Args == null || arg.Args.Length < 2 ) { SendReply(arg, "Usage: lootmultiplier <x> <all | tables | heli | bradley | fancy> | Multiplies the default loot by 'x'"); return; } int num = -1; int.TryParse(arg.Args[0], out num); if (num < 1) { SendReply(arg, "The multiplier must be a full number and greater then zero"); return; } data = arg.Args[1];  if (data == "tables" || data == "all") { try { lootTable = getFile("LootTables"); } catch (JsonReaderException e) { PrintWarningCl($"JSON error in 'LootTables' > Line: {e.LineNumber} | {e.Path}", arg); pluginEnabled = false; return; } var lootcont = lootTable.Get("LootContainer") as Dictionary <string,object>; if (data == "all") { if (enableHeliLoadOut) GetHeliLoot(true, num); if (enableBradleyLoadOut) GetBradleyLoot(true, num); if (FancyDrop && enableFancyDropLoot) GetFancyDropLoot(new Dictionary<string, object>(), true, num); } foreach (var cont in lootcont) { (lootcont[cont.Key] as Dictionary <string,object>)["UseMultiplier"] = true; (lootcont[cont.Key] as Dictionary <string,object>)["UsedMultiplier"] = num; LootContainer loot = GameManager.server.FindPrefab(AllContainerTypes[cont.Key]).GetComponent<LootContainer>(); (lootcont[cont.Key] as Dictionary<string, object>)["ScrapAmount"] = loot.scrapAmount * num; (lootcont[cont.Key] as Dictionary<string, object>)["ScrapVariance"] = Mathf.CeilToInt(loot.scrapAmount * num / 2); loot = null; } lootTable.Clear(); lootTable.Set("LootContainer", lootcont); lootTable.Save(); if (data == "all") SendReplyCl(arg, $"All LootTables successfully recreated"); else SendReplyCl(arg, $"LootTables.json successfully recreated"); LoadConfig(); LoadVariables(); NextFrame(()=> ServerMgr.Instance.StartCoroutine(UpdateInternals(true, arg : arg))); } else if (data == "heli") { if (!enableHeliLoadOut) { SendReply(arg, $"Aborted. HeliLoadOut not enabled", arg); return; } GetHeliLoot(true, num); SendReply(arg, $"HeliLoot successfully multiplied by '{num}' and being activated"); } else if (data == "bradley") { if (!enableBradleyLoadOut) { SendReply(arg, $"Aborted. BradleyLoadOut not enabled"); return; } GetBradleyLoot(true, num); SendReply(arg, $"BradleyLoot successfully multiplied by '{num}' and being activated"); } else if (data == "fancy") { if (!FancyDrop || (FancyDrop && !enableFancyDropLoot) || (!FancyDrop && enableFancyDropLoot)) { SendReply(arg, $"Aborted. FancyDropLoot not enabled"); return; } GetFancyDropLoot(new Dictionary<string, object>(), true, num); SendReply(arg, $"FancyDropLoot successfully multiplied by '{num}' and being activated");  } else SendReply(arg, "You need to provide 'all', 'tables', 'heli', 'bradley' or 'fancy'"); }  [ConsoleCommand("al.searchitems")] void consoleSearchItems(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (arg.Args == null || arg.Args.Length != 1) { SendReply(arg, "Usage: searchitems <partial name|itemid>"); return; } string searchFor = arg.Args[0].ToLower(); int count = 0; TextTable textTable = new TextTable(); textTable.AddColumn("Shortname"); textTable.AddColumn("Displayname"); textTable.AddColumn("ItemId"); textTable.AddColumn("Category"); textTable.AddColumn("Rarity/Default"); List<ItemDefinition> addedDefs = new List<ItemDefinition>(); foreach (var itemDef in ItemManager.GetItemDefinitions()) { if (itemDef.shortname.Contains(searchFor) || itemDef.displayDescription.english.ToLower().Contains(searchFor) || itemDef.displayName.english.ToLower().Contains(searchFor) || itemDef.itemid.ToString().Contains(searchFor)) { if (addedDefs.Contains(itemDef)) continue; addedDefs.Add(itemDef); count++; textTable.AddRow(new string[] { itemDef.shortname, itemDef.displayName.english, itemDef.itemid.ToString(), itemDef.category.ToString(), (rarityOverridesItems.ContainsKey(itemDef.shortname) ? (int)rarityOverridesItems[itemDef.shortname] : (int)itemDef.rarity).ToString()+"/"+((int)itemDef.rarity).ToString() }); } if ( count > 15 ) break; } string result = ""; if (count > 15) result = $"\nSearchresult for '{searchFor}' returned more then 15 hits:"; else result = $"\nSearchresult for '{searchFor}':"; SendReply(arg, result + "\n"+textTable.ToString()); }  [ConsoleCommand("al.shownewitems")] void consoleShowNewItems(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (showNewGameItems == null || showNewGameItems.Count == 0) { SendReply(arg, "Now new items found OR the 'NamesList.json' did not exist before the recent patch"); return; } TextTable textTable = new TextTable(); textTable.AddColumn("Shortname"); textTable.AddColumn("Displayname"); textTable.AddColumn("ItemId"); textTable.AddColumn("Category"); textTable.AddColumn("Rarity/Default"); foreach (var itemDef in showNewGameItems) { textTable.AddRow(new string[] { itemDef.shortname, itemDef.displayName.english, itemDef.itemid.ToString(), itemDef.category.ToString(), (rarityOverridesItems.ContainsKey(itemDef.shortname) ? (int)rarityOverridesItems[itemDef.shortname] : (int)itemDef.rarity).ToString()+"/"+((int)itemDef.rarity).ToString() }); } SendReply(arg, "\n"+textTable.ToString()); }   [ConsoleCommand("al.itemremove")] void consoleItemRemove(ConsoleSystem.Arg arg) { if (arg.Connection != null && arg.Connection.authLevel < setupAuthLevel) return; if (arg.Args == null || arg.Args.Length != 1) { SendReply(arg, "Usage: itemremove shortname"); return; } if (ItemManager.FindItemDefinition(arg.Args[0].ToLower()) == null) { SendReply(arg, $"Item name '{arg.Args[0].ToLower()}' not found in game"); return; } var name = arg.Args[0].ToLower(); int modified = 0; foreach (var data in lootData) { foreach (var limit in (data.Value as Dictionary<string, object>).ToList()) { if (name == limit.Key.ToString()) { (lootData[data.Key] as Dictionary<string, object>).Remove(limit.Key); modified++; } } } if (modified > 0) { SendReply(arg, $"Removed '{modified}'entries for item: '{name}'"); SendReply(arg, $"New 